<?php

namespace App\Enum;

enum PostType: string
{
    case ARTICLE = 'article';
    case PROJECT = 'project';
    case SHOWCASE = 'showcase';
    case RESOURCE = 'resource';
    //case QUESTION = 'question';
    //case JOB = 'job';
    case EVENT = 'event';
    case NEWS = 'news';

    public function getLabel(): string
    {
        return match ($this) {
            self::ARTICLE => 'مقاله',
            self::PROJECT => 'پروژه',
            self::SHOWCASE => 'نمایش دستاورد',
            self::RESOURCE => 'منبع آموزشی',
            //self::QUESTION => 'سوال',
            //self::JOB => 'موقعیت شغلی',
            self::EVENT => 'رویداد',
            self::NEWS => 'خبر',
        };
    }

    public function getDescription(): string
    {
        return match ($this) {
            self::ARTICLE => 'محتوای آموزشی یا تحلیلی نوشتاری (پست وبلاگ) در مورد موضوعات مختلف',
            self::PROJECT => 'پروژه‌ای که در حال توسعه هست و میتونه برای جامعه جالب و مفید باشه',
            self::SHOWCASE => 'نمایش کار تکمیل شده خودت که می‌خوای با جامعه به اشتراک بذاری',
            self::RESOURCE => 'معرفی ابزار، کتابخونه، ویدیوهای آموزشی، یا منابع مفید برای دیگر توسعه‌دهندگان',
            //self::QUESTION => 'پرسش فنی یا درخواست راهنمایی از جامعه',
            //self::JOB => 'آگهی استخدام یا فرصت‌های همکاری',
            self::EVENT => 'معرفی رویدادها، گردهمایی‌ها، یا وبینارهای داخلی یا بین‌المللی',
            self::NEWS => 'اخبار مرتبط با دنیای فناوری، هوش مصنوعی، کامپیوتر و توسعه نرم‌افزار',
        };
    }

